import { useState } from 'react'
import { errors, setErrors} from './components/Form'


const validate = () => {
    if(!userData.email){
        setErrors({
            ...errors,
            email: 'Este campo no puede quedar en blanco'
        })
    }
    if(!/\S+@\S+\.\S+/.test(userData.email)) {
        setErrors({
            ...errors,
            email: 'el usuario tiene que ser un email'
        })
      }
    if(userData.email.length > 35){
        setErrors({
            ...errors,
            email: 'el usuario no puede tener más de 35 caracteres'
        })
    }
    if(!/^(?=.*[0-9]).{6,10}$/.test(userData.password)){
        setErrors({
            ...errors,
            password: 'el password debe contener al menos un número y entre 6 y 10 caracteres'
        })
    }
      else errors.email = '';
}

export default validate
