import {ADD_FAV, REMOVE_FAV} from './actions-type'


const initialState = {
    myFavorites: [],
    allCharacters: []
}

const reducer = (state = initialState, {type, payload}) => {
    switch(type) {
        case ADD_FAV:
            return{
                ...state,
                allCharacters: [...state.myFavorites, payload]
            }
        case REMOVE_FAV:
            return{
                ...state,
                myFavorites: state.myFavorites.
                filter(fav => fav.id !== payload)
            }

        default:
            return{
                ...state
            }        
    }
}

export default reducer
